import styled from "styled-components";

export const SectionStyled = styled.section`
    width: 50%;
    max-width: 380px;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;

    div{
        width: 100%;
        max-width: 380px;
        /* max-height: 200px; */
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        gap: 25px;
    }
    
    div > img {
        max-height: 55px;
        min-width: 240px;
    }

    div > div {
        width: 95%;
        display: flex;
        flex-direction: row;
        align-items: center;
    }

    div > div > figure {
        height: 60px;
        width: 60px;
        padding: 18px;
        border-radius: 5px;
        background-color: #27AE6010;
    }

    div > div > p {
        font-size: 14px;
        font-weight: 400;
        line-height: 22px;
        color: #999999;
    }
`
