import styled from "styled-components";
import InputComponent from ".";

export const InputStyled = styled(InputComponent)`
    display: flex;
    flex-direction: column;

    label {
        font-size: 12px;
        font-weight: 400;
        color: #999999;
    }

    input{
        height: 60px;
        width: 452px;
        border-radius: 8px;
    }
`
