import styled from "styled-components";

export const SectionForm = styled.section `
    width: 50%
    max-width: 500px;
    max-height: 530px;
    display: flex;
    flex-direction: column;
    border: 2px solid #F5F5F5;
    padding: 20px;
    border-radius: 5px;
    gap: 10px;

    div {
        display: flex;
        flex-direction: row;
        align-items: center;
    }

    div > h2 {
        font-size: 18px;
        font-weight: 700;
        color: #333333;
    }
`
